import React, { useState, useEffect } from 'react';
import HomePage from './HomePage';
import FlashCardsPage from './FlashCardsPage';
import './App.css';
import axios from 'axios';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

function App() {
  const [flashcards, setFlashcards] = useState(EXAMPLE_FLASHCARDS);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://opentdb.com/api.php?amount=10');

        if (response && response.data && response.data.results) {
          setFlashcards(
            response.data.results.map((questionItem, index) => {
              const answer = decodeString(questionItem.correct_answer);
              const options = [
                ...questionItem.incorrect_answers.map(a => decodeString(a)),
                answer,
              ];
              return {
                id: `${index}-${Date.now()}`,
                question: decodeString(questionItem.question),
                answer: answer,
                options: options.sort(() => Math.random() - 0.5),
              };
            })
          );
        } else {
          console.error('Unexpected response format:', response);
        }
      } catch (error) {
        if (error.response && error.response.status === 429) {
          console.error('Rate limit exceeded. Please try again later.');
        } else {
          console.error('Error fetching data:', error.message);
        }
      }
    };

    fetchData();
  }, []);

  function decodeString(str) {
    const textArea = document.createElement('textarea');
    textArea.innerHTML = str;
    return textArea.value;
  }

  return (
    <Router>
      <div className="container">

        <Switch>
          <Route path="/" exact component={HomePage} />
          <Route path="/flashcards" render={() => <FlashCardsPage flashcards={flashcards} />} />

        </Switch>
      </div>
    </Router>
  );
}

const EXAMPLE_FLASHCARDS = [
  {
    id: 1,
    question: 'What is 5+5',
    answer: '10',
    options: ['2', '7', '8', '10'],
  },
  {
    id: 2,
    question: 'Question2',
    answer: 'Answer',
    options: ['Answer 1', 'Answer 2', 'Answer 3', 'Answer'],
  },
];

export default App;
