import React, { useState } from 'react';

export default function AddFlashcardModal({ onSave, onClose }) {
  const [newQuestion, setNewQuestion] = useState('');
  const [newAnswer, setNewAnswer] = useState('');

  const handleSave = () => {
    onSave(newQuestion, newAnswer);
    onClose();
  };

  return (
    <div className="add-modal">
      <h2>Add New Flashcard</h2>
      <label>
        Question:
        <input type="text" value={newQuestion} onChange={(e) => setNewQuestion(e.target.value)} />
      </label>
      <label>
        Answer:
        <input type="text" value={newAnswer} onChange={(e) => setNewAnswer(e.target.value)} />
      </label>
      <button onClick={handleSave}>Save</button>
      <button onClick={onClose}>Cancel</button>
    </div>
  );
}
