// FlashCardsPage.js

import React, { useState } from 'react';
import Flashcard from './Flashcard';
import EditModal from './EditModal'; // Import the EditModal component

export default function FlashCardsPage({ flashcards }) {
  const [modifiedCards, setModifiedCards] = useState([]);
  const [editModalOpen, setEditModalOpen] = useState(false); // State to manage the edit modal
  const [editedCardId, setEditedCardId] = useState(null); // State to store the ID of the card being edited

  const updateFlashcardStatus = (cardId, newStatus) => {
    setModifiedCards((prevModifiedCards) => [...prevModifiedCards, cardId]);
  };

  const onCardModified = (cardId) => {
    console.log(`Card with ID ${cardId} was modified.`);
  };

  // Open the edit modal when the "Edit" button is clicked
  const handleEditModalOpen = (cardId) => {
    setEditedCardId(cardId);
    setEditModalOpen(true);
  };

  // Close the edit modal
  const handleEditModalClose = () => {
    setEditModalOpen(false);
    setEditedCardId(null);
  };

  // Handle saving the edited content
  const handleEditModalSave = (editedQuestion, editedAnswer) => {
    // Update the flashcards state with the edited content
    setFlashcards((prevFlashcards) =>
      prevFlashcards.map((flashcard) =>
        flashcard.id === editedCardId
          ? {
              ...flashcard,
              question: editedQuestion,
              answer: editedAnswer,
              lastModified: new Date().toISOString(),
            }
          : flashcard
      )
    );
    handleEditModalClose(); // Close the modal after saving
  };

  // ... (rest of the component)

  return (
    <div className="card-grid">
      {flashcards.map((flashcard) => (
        <Flashcard
          key={flashcard.id}
          flashcard={flashcard}
          updateFlashcardStatus={updateFlashcardStatus}
          onCardModified={onCardModified}
          onEdit={handleEditModalOpen} // Pass the edit function as a prop
        />
      ))}

      {/* Render the edit modal when open */}
      {editModalOpen && (
        <EditModal
          flashcard={flashcards.find((card) => card.id === editedCardId)}
          onSave={handleEditModalSave}
          onClose={handleEditModalClose}
        />
      )}
    </div>
  );
}
