import React, { useState, useEffect } from 'react';
import Flashcard from './Flashcard';
import EditFlashcardModal from './EditFlashcardModal';

export default function FlashCardsPage({ flashcards, setFlashcards }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredFlashcards, setFilteredFlashcards] = useState([]);
  const [editingFlashcard, setEditingFlashcard] = useState(null);

  useEffect(() => {

    filterFlashcards();
  }, [searchQuery, flashcards]);

  const filterFlashcards = () => {

    const lowerCaseQuery = searchQuery.toLowerCase();
    const filtered = flashcards.filter(
      (card) =>
        card.question.toLowerCase().includes(lowerCaseQuery) ||
        card.answer.toLowerCase().includes(lowerCaseQuery)
    );
    setFilteredFlashcards(filtered);
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleEdit = (cardId, editedQuestion, editedAnswer) => {

    console.log(`Editing flashcard with ID ${cardId}: ${editedQuestion}, ${editedAnswer}`);
  };

  const handleDelete = (cardId) => {

    console.log(`Deleting flashcard with ID ${cardId}`);
  };

  const handleSaveEdit = (cardId, editedQuestion, editedAnswer) => {

    console.log(`Saving edited flashcard with ID ${cardId}: ${editedQuestion}, ${editedAnswer}`);
    setEditingFlashcard(null);
  };

  const handleEditClick = (cardId) => {

    setEditingFlashcard(cardId);
  };

  const handleCloseEditModal = () => {
    setEditingFlashcard(null);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search..."
        value={searchQuery}
        onChange={handleSearchChange}
      />

      {filteredFlashcards.map((flashcard) => (
        <Flashcard
          key={flashcard.id}
          flashcard={flashcard}
          onEditClick={handleEditClick}
          onDelete={handleDelete}
        />
      ))}

      {editingFlashcard && (
        <EditFlashcardModal
          flashcard={flashcards.find((card) => card.id === editingFlashcard)}
          onSave={handleSaveEdit}
          onClose={handleCloseEditModal}
        />
      )}
    </div>
  );
}
