// FlashCardsPage.js

import React, { useState } from 'react';
import Flashcard from './Flashcard';
import EditModal from './EditModal'; // Import the EditModal component

export default function FlashCardsPage({ flashcards }) {
  const [editCardId, setEditCardId] = useState(null); // New state to track the card being edited

  const updateFlashcardStatus = (cardId, newStatus) => {
    setModifiedCards((prevModifiedCards) => [...prevModifiedCards, cardId]);
  };

  const onCardModified = (cardId) => {
    console.log(`Card with ID ${cardId} was modified.`);
    setEditCardId(null); // Reset edit state after modification
  };

  const onEdit = (cardId) => {
    setEditCardId(cardId); // Set the cardId to be edited
  };

  // Add delete functionality here

  return (
    <div className="card-grid">
      {flashcards.map((flashcard) => (
        <React.Fragment key={flashcard.id}>
          <Flashcard
            flashcard={flashcard}
            updateFlashcardStatus={updateFlashcardStatus}
            onCardModified={onCardModified}
            onEdit={onEdit}
          />
          {editCardId === flashcard.id && (
            <EditModal flashcard={flashcard} setEditCardId={setEditCardId} />
          )}
        </React.Fragment>
      ))}
    </div>
  );
}
