import React, { useState, useEffect } from 'react';
import { fetchCards, addCard, updateCard, deleteCard } from '../services/api';
import FlashCard from './FlashCard';

const FlashCardsPage = () => {
  const [cards, setCards] = useState([]);
  const [isNewCardFormVisible, setNewCardFormVisible] = useState(false);
  const [editingCard, setEditingCard] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const response = await fetchCards();
    setCards(response.data);
  };

  const handleAddCard = async (newCard) => {
    await addCard(newCard);
    setNewCardFormVisible(false);
    fetchData();
  };

  const handleEditCard = (card) => {
    setEditingCard(card);
  };

  const handleUpdateCard = async (updatedCard) => {
    await updateCard(updatedCard);
    setEditingCard(null);
    fetchData();
  };

  const handleDeleteCard = async (cardId) => {
    await deleteCard(cardId);
    fetchData();
  };

  return (
    <div className="flash-cards-page">
      {/* Display the "Add Card" button and form */}
      <button onClick={() => setNewCardFormVisible(true)}>Add Card</button>
      {isNewCardFormVisible && (
        <NewCardForm onSave={handleAddCard} onCancel={() => setNewCardFormVisible(false)} />
      )}

      {cards.map((card) => (
        <FlashCard
          key={card.id}
          card={card}
          isEditing={editingCard && editingCard.id === card.id}
          onEdit={() => handleEditCard(card)}
          onUpdate={handleUpdateCard}
          onDelete={() => handleDeleteCard(card.id)}
        />
      ))}
    </div>
  );
};

export default FlashCardsPage;
