import React, { useState } from 'react';
import Flashcard from './Flashcard';
import EditFlashcardModal from './EditFlashcardModal';

export default function FlashCardsPage({ flashcards, setFlashcards }) {
  const [modifiedCards, setModifiedCards] = useState([]);
  const [editingFlashcard, setEditingFlashcard] = useState(null);

  const updateFlashcardStatus = (cardId, newStatus) => {
    setModifiedCards((prevModifiedCards) => [...prevModifiedCards, cardId]);
  };

  const onCardModified = (cardId) => {
    console.log(`Card with ID ${cardId} was modified.`);
  };

  const handleDelete = (cardId) => {
    // Remove the flashcard from the collection
    const updatedFlashcards = flashcards.filter((card) => card.id !== cardId);

    // Set the updated flashcards
    setFlashcards(updatedFlashcards);

    // Reset the editing state
    setEditingFlashcard(null);
  };

  const handleSaveEdit = (cardId, editedQuestion, editedAnswer) => {
    // Save the edited content of the flashcard
    const updatedFlashcards = flashcards.map((card) =>
      card.id === cardId
        ? {
            ...card,
            question: editedQuestion,
            answer: editedAnswer,
            lastModified: new Date().toISOString(),
          }
        : card
    );

    // Set the updated flashcards
    setFlashcards(updatedFlashcards);

    // Reset the editing state
    setEditingFlashcard(null);
  };

  return (
    <div className="card-grid">
      {flashcards.map((flashcard) => (
        <div key={flashcard.id} className="card-container">
          <Flashcard
            flashcard={flashcard}
            updateFlashcardStatus={updateFlashcardStatus}
            onCardModified={onCardModified}
            onEdit={() => setEditingFlashcard(flashcard)}
          />
          {editingFlashcard === flashcard && (
            <EditFlashcardModal
              flashcard={flashcard}
              onSave={handleSaveEdit}
              onClose={() => setEditingFlashcard(null)}
            />
          )}
          <div className="card-actions">
            <button onClick={() => handleDelete(flashcard.id)}>Delete</button>
          </div>
        </div>
      ))}
    </div>
  );
}
